import Button from "./Button";
let productCount =2;
function displayFormattedProductCount (){
    return (productCount > 0 ? productCount : <p>zero</p> );
}
function ProductDetails (props) 
{
    let badgeclass = 'badge-margin-left-40 badge ';
    badgeclass += props.isAvailable ? 'bg-success' : 'bg-danger';

    return (

        <div className="d-flex align-items-center justify-content-start mt-1">
        <h6 className="font-weight-bold my-2">{props.price}</h6>
        <Button>-</Button>
        <span style={{padding :'0px 14px' , 'font-size': 13}}>{displayFormattedProductCount()}</span>
        <Button>+</Button>
        <span className={badgeclass}>{props.isAvailable ? 'Available': 'UnAvailable'}</span>
      </div>

    );
}
export default ProductDetails;
