import { useState } from "react";
function ProductForm ()
{
    let [pName , updateName]= useState('');
let [pPrice , updatePrice]= useState('');
let [pDesc , updateDesc]= useState('');
let [pAvailability , updateAvailability]= useState('');
let [pimage , updateimage]= useState('');
    function nameChangeHandler(props)
    {
        updateName(props.target.value);
    }
    function priceChangeHandler(props)
    {
        updatePrice(props.target.value);
    }
    function descChangeHandler(props)
    {
        updateDesc(props.target.value);
    }
    function AvailabilityChangeHandler(props)
    {
        updateAvailability(props.target.checked);
    }
    function imageChangeHandler(props)
    {
        updateimage(props.target.value);
    }
    function submitHandler (event) {
           event.preventDefault();
           let product = {
            pID: 1, 
            pName: pName, 
            desc: pDesc,
            isAvailable: pAvailability,
            image: pimage,
            price: pPrice
           }
           console.log(product);
    }
    return (
        <form className="row g-3" onSubmit= {submitHandler}>
        <div className="col-md-6">
            <label for="name">Product Name</label>
            <input type="text" 
                    className="form-control" 
                    id="name" 
                    placeholder="Product Name" 
                    onChange={nameChangeHandler}/>
        </div>
        <div className="col-md-6">
            <label for="price">Product Price</label>
            <input type="number" 
                    min="0.01" step="0.01" 
                    className="form-control" 
                    id="price" 
                    placeholder="Product Price" 
                    onChange={priceChangeHandler}/>
        </div>

        <div className="form-group">
            <label for="description">Product Description</label>
            <input type="text" 
                    className="form-control" 
                    id="description" 
                    placeholder="Product Description"
                    onChange={descChangeHandler} />
        </div>

        <div class="form-check form-switch">
            <input class="form-check-input" type="checkbox" role="switch" id="isAvailable" onChange={AvailabilityChangeHandler}/>
            <label class="form-check-label" for="isAvailable">Is product available in stock?</label>
        </div>

        <div className="form-group">
            <label for="select-image">Select product image</label>
            <input type="file" className="form-control" id="select-image" onChange={imageChangeHandler}/>
        </div>


        <button type="submit" className="btn btn-primary">Add Product</button>
    </form>
    );

}
export default ProductForm;
